import React, { useContext } from "react";
import { GameStateContext, ThemeContext } from "./App";

export default function NavigationButtons() {
  const { gameState, setGameState } = useContext(GameStateContext);
  const { darkTheme } = useContext(ThemeContext);

  function goToCharacterScreen() {
    if (gameState !== "character-creation") {
      setGameState("character-screen");
    }
  }

  function goToStory() {
    if (gameState !== "character-creation") {
      setGameState("story");
    }
  }

  function goToMenu() {
    if (gameState !== "character-creation") {
      setGameState("menu");
    }
  }

  function goToSettings() {
    if (gameState !== "character-creation") {
      setGameState("settings");
    }
  }

  const buttonStyle = {
    border: darkTheme.enabled ? "solid 0.1rem white" : "solid 0.1rem black",
    borderRadius: "25%",
  };

  return (
    <div className='nav-buttons'>
      <button
        className='nav-btn'
        style={buttonStyle}
        onClick={goToCharacterScreen}
      >
        Character
      </button>
      <button className='nav-btn' style={buttonStyle} onClick={goToStory}>
        Story
      </button>
      <button className='nav-btn' style={buttonStyle} onClick={goToMenu}>
        Menu
      </button>
      <button className='nav-btn' style={buttonStyle} onClick={goToSettings}>
        Settings
      </button>
    </div>
  );
}
