import React, { useContext } from "react";
import { GameStateContext, ThemeContext } from "./App";

export default function NavigationButtons() {
  const { gameState, setGameState } = useContext(GameStateContext);
  const { borderStyle } = useContext(ThemeContext);

  function goToCharacterScreen() {
    if (gameState !== "character-creation") {
      setGameState("character-screen");
    }
  }

  function goToStory() {
    if (gameState !== "character-creation") {
      setGameState("story");
    }
  }

  function goToMenu() {
    if (gameState !== "character-creation") {
      setGameState("menu");
    }
  }

  function goToSettings() {
    if (gameState !== "character-creation") {
      setGameState("settings");
    }
  }

  return (
    <div className='nav-buttons' style={borderStyle}>
      <button className='nav-btn' onClick={goToCharacterScreen}>
        Character
      </button>
      <button className='nav-btn' onClick={goToStory}>
        Story
      </button>
      <button className='nav-btn' onClick={goToMenu}>
        Menu
      </button>
      <button className='nav-btn' onClick={goToSettings}>
        Settings
      </button>
    </div>
  );
}
